import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import BreadCrumb from "../components/BreadCrumb";
import ProductCarousel from "../components/ProductCarousel";
import { useParams } from "react-router-dom";
import axios from "axios";
function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const getProduct = async () => {
    await axios
      .get("http://localhost:3000/api/v1/products/" + id)
      .then((res) => setProduct(res.data))
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getProduct();
  }, []);
  return (
    <div className="ProductPage">
      <Navbar></Navbar>
      <BreadCrumb />
      <section className="container product-detail">
        <div className="row">
          <h3 className="product-name">Tổ yến tinh chế vip 1 </h3>
          <i className="sku">SKU: TC123123</i>
          <p className="mini-description">
            <span className="mini-description-item product-origin">
              Mr. Hà Chuyên bán yến
            </span>
            <span className="mini-description-item product-status">
              Còn hàng
            </span>
            <span className="mini-description-item product-sell">
              Đã bán : 21 sản phẩm
            </span>
          </p>
        </div>
        {/* rouw 2 */}
        <div className="row">
          <div className="col-5">
            {product?.source?.length > 0 && (
              <ProductCarousel source={product.source} />
            )}
          </div>
          <div className="col-4"></div>
          <div className="col-3"></div>
        </div>
      </section>
    </div>
  );
}

export default ProductPage;
